import React from 'react';
import { View, Text, StyleSheet, ScrollView, Image, TouchableOpacity } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';

import Produtos from '../../components/Produtos';

export default function Home() {
    return (
        <View style={styles.container}>
            <View style={styles.header}> 
               <Image
                   source={require('../../assets/banner.png')}
                   style={styles.image}
               />

            <View style={styles.textContainer}>
                <Text style={styles.text}>Categoria</Text>
                <Text style={styles.text, { color: '#CECECF'}}>-</Text>
                <Text style={styles.text}>Fogão</Text>
                <TouchableOpacity style={{position: 'absolute',right: 0, alignSelf: 'center'}}>
                    <MaterialIcons
                      name="filter-list"
                      size={24}
                      color="#000"
                    />
                </TouchableOpacity>
            </View>
            </View>


            <View styles={styles.line} />

            <ScrollView>
               <Text style={styles.text}>Lançamentos</Text>

               <View style={{ flexDirection: 'row', justifyContent: 'space-around'}}>
                   <Produtos img ={require('../../assets/FogaoAtlas.png')} cost="R$500,00">
                       Fogão Atlas Padrão 4 bocas cor preto
                   </Produtos>                   
                    <Produtos img ={require('../../assets/FogaoBrastemp.png')} cost="R$600,00">
                       Fogão Brastemp
                   </Produtos>
                </View>

                <View style={{ flexDirection: 'row', justifyContent: 'space-around'}}>
                   <Produtos img ={require('../../assets/FogaoConsul.png')} cost="R$500,00">
                       Fogão Consul
                   </Produtos>  

                   <Produtos img ={require('../../assets/LavadoraBrastemp.png')} cost="R$500,00">
                       Lavadora Brastemp
                   </Produtos>  
                </View>

                <View style={{ flexDirection: 'row', justifyContent: 'space-around'}}>
                   <Produtos img ={require('../../assets/LavadoraPhilco.png')} cost="R$500,00">
                       Lavadora Philco
                   </Produtos>  

                   <Produtos img ={require('../../assets/LavaloucaBrastemp.png')} cost="R$500,00">
                       Lava-louça Brastemp
                   </Produtos>  
                </View>


            </ScrollView>

        </View>

       

    );
}

 const styles = StyleSheet.create({
     container:{
        flex:1,
        width:'100%',
        backgroundColor:"#fff"
     },
     header:{
         marginBottom: 8
     },

     image:{
         width: '100%'
     },
     textContainer:{
         flexDirection: 'row',
         marginVertical: '5%',
         marginHorizontal: '5%'
     },
     text:{
      fontFamily: 'Anton_400Regular',
      fontSize: 26,
      marginHorizontal: '1%'
     },
     line:{
         borderBottomColor: '#d8d8d8',
         borderBottomWidth: 2

     }


 });
