import React from 'react'
import { View, Text, TouchableOpacity, Image, StyleSheet } from 'react-native'

export default function Produtos() {

    function filterDesc(desc){
        if(desc.length < 27){
            return desc;
        }
        return `${desc.substring(0, 22)}...`;
    }
    return (
       <TouchableOpacity style={styles.container}>
           <Image 
            source={require('../../assets/FogaoAtlas.png')}
            style={styles.produtoImg}
           />

           <Text style={styles.produtoText}>
              {filterDesc('Fog達o Atlas Padr達o 4 bocas cor preto')} 
           </Text>

           <View opacity={0.4}>
           <Text style={styles.produtoText}>R$500,00</Text>
           </View>
           
       </TouchableOpacity>
    );
}

const styles = StyleSheet.create({
    container: {
        paddingVertical: '2%',
        alignItems: 'center',
        justifyContent: 'center'
    },
    produtoImg:{
        width: 175,
        height: 175
    },
    produtoText:{
        fontSize:16
    }
});
